import React, { useEffect, useState, useCallback } from 'react'; // Import useCallback
import { Col, Card } from 'react-bootstrap';
import './BookDetails.css';

function BookDetails({ selectedCafe }) {
  const [imageSrc, setImageSrc] = useState(null);
  const [cafeDetails, setCafeDetails] = useState({});

  const fetchCafeDetails = useCallback(async () => { // Use useCallback here
    if (selectedCafe && selectedCafe.id) {
      const response = await fetch(`http://localhost:3001/cafes/${selectedCafe.id}`);
      if (!response.ok) {
        throw new Error("Error al obtener los detalles del café");
      }
      const data = await response.json();
      setCafeDetails(data);  // Set the cafe details
    }
  }, [selectedCafe]);

  useEffect(() => {
    fetchCafeDetails();
  }, [fetchCafeDetails]); // Corrected the dependency array here

  useEffect(() => {
    if (selectedCafe && selectedCafe.id) {
      fetch(`/api/cafes/${selectedCafe.id}/imagen`)
        .then(response => response.blob())
        .then(blob => {
          const url = URL.createObjectURL(blob);
          setImageSrc(url);
        })
        .catch(error => console.error('Error fetching image:', error));
    }
  }, [selectedCafe]);

  return (
    <Col md={4}>
      <div className="cafe-details"> 
        <Card>
          <Card.Body>
            <Card.Text>
              <b>Nombre: {cafeDetails.nombre || ''}</b>
            </Card.Text>
            <Card.Text className="cafe-text">
              <b>{cafeDetails.fecha_cultivo || ''}</b>
            </Card.Text>
            {imageSrc && (
              <div className='cafe-image'>
                <img src={cafeDetails.imagen} alt="Imagen del café" className='cafe-image-img'/>
              </div>
            )}
            <Card.Text>
              <b>Notas: {cafeDetails.notas || ''}</b>
            </Card.Text>
            <Card.Text>
              <b>Cultivado a una altura de: {cafeDetails.altura || ''}</b>
            </Card.Text>
          </Card.Body>
        </Card>
      </div>
    </Col>
  );
}

export default BookDetails;
