import React from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import './BookCatalog.css';  // Cambiado de BookCatalog.css a BookCatalog.css

function BookCatalog({ cafes, onSelectCafe, selectedCafe }) {  // Cambiado de BookCatalog a BookCatalog y otros nombres relacionados
  return (
    <Col md={6}>
      <h2>Catálogo de Cafés</h2>
      <Row className="cafe-row">
        {cafes.map((cafe) => (
          <Col md={4} key={cafe.id} className="cafe-col">
            <Card
              onClick={() => onSelectCafe(cafe)}
              className={`cafe-card ${
                selectedCafe && selectedCafe.id === cafe.id ? "selected" : ""
              }`}  // Cambiado de book-card a cafe-card y de book.isbn a cafe.id
            >
              <Card.Body className="cafe-card-body">
                <Card.Title>{cafe.nombre}</Card.Title>
                <Card.Text>{cafe.tipo}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Col>
  );
}

export default BookCatalog;  // Cambiado de BookCatalog a BookCatalog
