import React from 'react';
import { ListGroup, Col } from 'react-bootstrap';
import './BookCatalog.css';  // Cambiado de BookCatalog.css a BookCatalog.css

function BookCatalog({ cafes, onSelectCafe, selectedCafe }) {  // Cambiado de BookCatalog a BookCatalog y otros nombres relacionados
  return (
    <Col md={6}>
      <h2>Catálogo de Cafés</h2>
      <ListGroup variant="flush" className="cafe-list">  {/* Añadido ListGroup con una clase CSS */}
        {cafes.map((cafe) => (
          <ListGroup.Item
            key={cafe.id}
            action  // Propiedad action para hacer los ítems clickeables
            onClick={() => onSelectCafe(cafe)}
            active={selectedCafe && selectedCafe.id === cafe.id}  // active determina si el ítem está seleccionado o no
          >
            {cafe.nombre} - {cafe.tipo}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Col>
  );
}

export default BookCatalog;  // Cambiado de BookCatalog a BookCatalog
