import React, { useEffect, useState } from 'react';
import { Col, Card } from 'react-bootstrap';
import './BookDetails.css';

function BookDetails({ selectedCafe, fieldValues }) {
  const [error, setError] = useState(null);
  const [imageSrc, setImageSrc] = useState(null);

  const fetchCafeDetails = async () => {
    if (selectedCafe && selectedCafe.id) {
      try {
        const response = await fetch(`http://localhost:3001/cafes/${selectedCafe.id}`);
        if (!response.ok) {
          throw new Error("Error al obtener los detalles del café");
        }
        const data = await response.json();
        console.log(data);
      } catch (error) {
        setError(error.message);
      }
    }
  };

  useEffect(() => {
    fetchCafeDetails();
  }, [selectedCafe]);

  useEffect(() => {
    if (selectedCafe && selectedCafe.id) {
      fetch(`/api/cafes/${selectedCafe.id}/imagen`)
        .then(response => response.blob())
        .then(blob => {
          const url = URL.createObjectURL(blob);
          setImageSrc(url);
        })
        .catch(error => console.error('Error fetching image:', error));
    }
  }, [selectedCafe]);

  return (
    <Col md={4}>
      <div className="cafe-details"> 
        <Card>
          <Card.Img variant="top" src={imageSrc} />
          <Card.Body>
            <Card.Text>
              <b>Nombre: {fieldValues.nombre || ''}</b>
            </Card.Text>
            <Card.Text>
              <b>Fecha de Cultivación: {fieldValues.fecha_cultivo || ''}</b>
            </Card.Text>
            <Card.Text>
              <b>Notas: {fieldValues.notas || ''}</b>
            </Card.Text>
            <Card.Text>
              <b>Cultivado a una altura de: {fieldValues.altura || ''}</b>
            </Card.Text>
          </Card.Body>
        </Card>
      </div>
    </Col>
  );
}

export default BookDetails;
