import React from 'react';
import { Col, Card } from 'react-bootstrap';
import './BookDetails.css';

function BookDetails({ selectedCafe }) {  // Eliminé fieldValues ya que deberías obtener los valores de selectedCafe
  return selectedCafe ? (
    <Col md={4}>
      <div className="cafe-details" style={{ textAlign: 'center' }}>  {/* Añadido style para centrar el contenido */}
        <Card>
          <Card.Body>
            <Card.Text>
              <b>Nombre: {selectedCafe.nombre || ''}</b>  {/* Añadido etiqueta <b> para negrilla */}
            </Card.Text>
            <Card.Text>
              Fecha de Cultivación: {selectedCafe.fecha_cultivo || ''}
            </Card.Text>
            {/* Añadido imagen del café */}
            <Card.Img src={selectedCafe.imagen || "https://github.com/Uniandes-isis2603/recursos-isis2603/blob/master/images/202310/p2_v1/cafe-especial-para-ti-cafe-colombiano_720x.png?raw=true"} alt="Imagen del café" />
            <Card.Text>
              <h3>Notas</h3>
              {selectedCafe.notas || ''}
            </Card.Text>
            <Card.Text>
              <h3 style={{ fontWeight: 'bold' }}>Cultivado a una altura de</h3>  {/* Añadido style para negrilla */}
              <b>{selectedCafe.altura || ''}</b>  {/* Añadido etiqueta <b> para negrilla */}
            </Card.Text>
          </Card.Body>
        </Card>
      </div>
    </Col>
  ) : null;
}

export default BookDetails;
