import React, { useEffect, useState } from 'react';
import { Col, Card } from 'react-bootstrap';
import './BookDetails.css';

function BookDetails({ selectedCafe, fieldValues }) {  // Agrega fieldValues aquí
  const [imageSrc, setImageSrc] = useState(null);

  useEffect(() => {
    if (selectedCafe && selectedCafe.id) {
      fetch(`/api/cafes/${selectedCafe.id}/imagen`)
        .then(response => response.blob())
        .then(blob => {
          const url = URL.createObjectURL(blob);
          setImageSrc(url);
        })
        .catch(error => console.error('Error fetching image:', error));
    }
  }, [selectedCafe]);

  // Utiliza los valores de fieldValues aquí en lugar de selectedCafe
  return (
    <Col md={4}>
      <div className="cafe-details"> 
        <Card>
          <Card.Body>
            <Card.Text>
              <b>Nombre: {fieldValues.nombre || ''}</b>
            </Card.Text>
            <Card.Text>
              <b>Fecha de Cultivación: {fieldValues.fecha_cultivo || ''}</b>
            </Card.Text>
            <Card.Text>
              <b>Notas: {fieldValues.notas || ''}</b>
            </Card.Text>
            <Card.Text>
              <b>Cultivado a una altura de: {fieldValues.altura || ''}</b>  {/* Corrige 'altuta' a 'altura' */}
            </Card.Text>
          </Card.Body>
        </Card>
      </div>
    </Col>
  );
}

export default BookDetails;
