import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

function Login() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ id: 1, email: "", password: "" });
  const [emailValid, setEmailValid] = useState(true);  // cambiar a true
  const [passwordValid, setPasswordValid] = useState(true);  // cambiar a true
  const [formTouched, setFormTouched] = useState(false);  // nuevo estado
  const [error, setError] = useState("");
  const [lastId, setLastId] = useState(1);

  const handleEmailChange = (e) => {
    setFormTouched(true);  // actualizar formTouched a true
    setFormData({ ...formData, email: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setFormTouched(true);  // actualizar formTouched a true
    setFormData({ ...formData, password: e.target.value });
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isEmailValid = emailRegex.test(email);
    setEmailValid(isEmailValid || email === "");
  };

  const validatePassword = (password) => {
    const isPasswordValid = password.length > 6;
    setPasswordValid(isPasswordValid || password === "");
  };

  useEffect(() => {
    validateEmail(formData.email);
    validatePassword(formData.password);
  }, [formData.email, formData.password]);

  async function handlePost() {
    if (!emailValid || !passwordValid || formData.email === "" || formData.password === "") {
      setError("El correo electrónico o la contraseña son inválidos");
      return;
    }

    // ... el resto de tu código
  }

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <img
            src="ruta_de_la_imagen.png"
            alt="Imagen de inicio de sesión"
            className="img-fluid"
          />
        </div>
        <div className="col-md-6">
          <h2>Iniciar Sesión</h2>
          {error && <div className="alert alert-danger">{error}</div>}
          <form>
            <div className="form-group">
              <label>Correo Electrónico</label>
              <input
                type="email"
                className={`form-control ${!emailValid && formTouched ? "is-invalid" : ""}`}
                value={formData.email}
                onChange={handleEmailChange}
              />
              {!emailValid && formTouched && <div className="invalid-feedback">Correo electrónico no válido</div>}
            </div>
            <div className="form-group">
              <label>Contraseña</label>
              <input
                type="password"
                className={`form-control ${!passwordValid && formTouched ? "is-invalid" : ""}`}
                value={formData.password}
                onChange={handlePasswordChange}
              />
              {!passwordValid && formTouched && (
                <div className="invalid-feedback">La contraseña debe ser de 6 caracteres</div>
              )}
            </div>
            <button type="button" className="btn btn-primary" onClick={handlePost}>
              Iniciar Sesión
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
