import React from 'react';
import { Col, Card } from 'react-bootstrap';
import './BookDetails.css';  // Cambiado de BookDetails.css a BookDetails.css

function BookDetails({ selectedCafe, userRole, fieldValues, onInputChange }) {  // Cambiado de BookDetails a BookDetails y otros nombres relacionados
  const renderEditableField = (label, field) => {
    const value = fieldValues[field];
    return userRole ? (
      <div>
        <div>
          <label className="BookDetails_label">{label}:</label>  // Cambiado de BookDetails_label a BookDetails_label
        </div>
        <input
          type="text"
          className="BookDetails_input"  // Cambiado de BookDetails_input a BookDetails_input
          value={value || ''}
          onChange={(e) => onInputChange(field, e.target.value)}
        />
      </div>
    ) : (
      <Card.Text>
        {label}: {value}
      </Card.Text>
    );
  };

  return selectedCafe ? (
    <Col md={6}>
      <div className="cafe-details">  // Cambiado de book-details a cafe-details
        <h2>Detalles del Café</h2>  // Cambiado de Detalles del Libro a Detalles del Café
        <Card>
          <Card.Body>
            {renderEditableField('Nombre', 'nombre')}  // Cambiado de Título a Nombre y de title a nombre
            {renderEditableField('Tipo', 'tipo')}  // Cambiado de Autor a Tipo y de author a tipo
            {renderEditableField('Región', 'region')}  // Nuevos campos según la estructura de datos de café
            {renderEditableField('Notas', 'notas')}
            {renderEditableField('Fecha de Cultivo', 'fecha_cultivo')}
            {renderEditableField('Altura', 'altura')}
          </Card.Body>
        </Card>
      </div>
    </Col>
  ) : null;
}

export default BookDetails;  // Cambiado de BookDetails a BookDetails
