import React from 'react';
import { Col, Card } from 'react-bootstrap';
import './BookDetails.css';

//quiero que se muestre la imagen del café luego del nombre

function BookDetails({ selectedCafe, fieldValues }) {
  return selectedCafe ? (
    <Col md={4}>
      <div className="cafe-details">
        <Card>
          <Card.Body>
            <Card.Text>
              Nombre: {fieldValues.nombre || ''}
            </Card.Text>
            <Card.Text>
              Imagen: {fieldValues.imagen || ''}
            </Card.Text>
            <Card.Text>
              Tipo: {fieldValues.tipo || ''}
            </Card.Text>
            <Card.Text>
              Región: {fieldValues.region || ''}
            </Card.Text>
            <Card.Text>
              Notas: {fieldValues.notas || ''}
            </Card.Text>
            <Card.Text>
              Fecha de Cultivo: {fieldValues.fecha_cultivo || ''}
            </Card.Text>
            <Card.Text>
              Altura: {fieldValues.altura || ''}
            </Card.Text>
          </Card.Body>
        </Card>
      </div>
    </Col>
  ) : null;
}

export default BookDetails;
