import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import banner from '../assets/banner.png';
import './Login.css';

function Login() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({ email: "", password: "" });
    const [error, setError] = useState("");

    const handleEmailChange = (e) => {
        setFormData({ ...formData, email: e.target.value });
    };

    const handlePasswordChange = (e) => {
        setFormData({ ...formData, password: e.target.value });
    };

    const handleCancel = () => {
        setFormData({ email: "", password: "" });
    };

    async function handleLogin() {
        const response = await fetch("http://localhost:3001/login", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                login: formData.email,
                password: formData.password
            })
        });

        const data = await response.json();

        if (response.status === 200) {
            navigate("/Home", { state: { userRole: "admin" } });  // Asumiendo el rol de admin
        } else {
            const errorMessage = data.message === "The provided credentials are incorrect."
                ? "Error de autenticación. Revise sus credenciales"
                : data.message;
            setError(errorMessage || "No se pudo iniciar sesión");
        }
    }

    return (
        <div>
            <div>
                <h2 className="titulo">El aroma mágico</h2>
                <hr />
            </div>
            <img src={banner} alt="Banner" className="banner-image" />
            <hr />
            <div>
        <b className="text-iniciarSesion">Inicio de sesión</b>
        <div className="container mt-3 custom-container">
          <div className="row justify-content-center">
            <div className="col-md-6">
              <form>
                <div className="form-group text-left">  {/* Clase text-left añadida */}
                  <label>Nombre de usuario</label>
                  <input
                    type="email"
                    className="form-control"
                    value={formData.email}
                    onChange={handleEmailChange}
                  />
                </div>
                <div className="form-group text-left">  {/* Clase text-left añadida */}
                  <label>Contraseña</label>
                  <input
                    type="password"
                    className="form-control"
                    value={formData.password}
                    onChange={handlePasswordChange}
                  />
                </div>
                <div className="button-group text-left">  {/* Clase text-left añadida */}
                  <button type="button" className="btn btn-success" onClick={handleLogin}>
                    Ingresar
                  </button>
                  <button type="button" className="btn btn-danger" onClick={handleCancel}>
                    Cancelar
                  </button>
                </div>
                {error && <div className="error-message text-left">{error}</div>}  {/* Clase text-left añadida */}
              </form>
            </div>
          </div>
        </div>
      </div>
      <div className="contact-info">
          Contact us: +57 3102105253 - info@elaromamagico.com - @elaromamagico
      </div>
        </div>
    );
}

export default Login;
